import React, { useState } from 'react';
import { eligibility } from './Data';

import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

import Button from 'react-bootstrap/Button';
import ProgressBar from 'react-bootstrap/ProgressBar';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import '../css/Eligibility.scss';

const Register = () => {
	let [currQuestion, setQuestion] = useState(0);
	let [active, setActive] = useState(-1);
	let [error, setError] = useState(false);
	let [answers, setAnswers] = useState([]);
	const [validated, setValidated] = useState(false);

	const submissionQuestions = [
		'This service is here to help me help myself.',
		'This service will NOT be giving me legal advice (unless purchased separately).',
		'This service is available to help both parties.',
	];

	const handleClick = (i) => {
		if (i === active) {
			setActive(-1);
		} else {
			setActive(i);
		}
	};

	const handleNext = () => {
		if (active === -1) {
			setError(true);
		} else {
			setAnswers([...answers, eligibility[currQuestion].answers[active]]);
			setError(false);
			setActive(-1);
			setQuestion(currQuestion + 1);
		}
	};

	const handleSubmit = (e) => {
		const form = e.currentTarget;
		if (form.checkValidity() === false) {
			e.preventDefault();
			e.stopPropagation();
		} else {
			console.log('submission proper!');
		}
		setValidated(true);
	};

	return (
		<div>
			<Navbar active="Pricing" />
			<div className="eligibility">
				<div className="question-container">
					{currQuestion < eligibility.length ? (
						<div>
							<ProgressBar
								now={(currQuestion / eligibility.length) * 100}
								className="progress"
							/>
							<h4>{eligibility[currQuestion].question}</h4>
							{eligibility[currQuestion].answers.map((answer, index) => (
								<Button
									className={`option ${index === active ? 'chosen' : null}`}
									onClick={() => handleClick(index)}
									key={answer}
								>
									<p>{answer}</p>
								</Button>
							))}
							<p className={`error ${error ? null : 'hidden'}`}>
								Please choose one of the above choices.
							</p>
							<div className="submit-container">
								<Button className="submit" onClick={handleNext}>
									<p>Next</p>
								</Button>
							</div>
						</div>
					) : (
						<div className="submission-page">
							<p>
								I certify that the above statement are true. I understand that
								unless I sign a separate Retainer Agreement with SKSR, they will
								have no responsibility for my case and will not represent me in
								court.
								<br />
								<br />U Do It Legal is here to assist people who represent
								themselves in court, without an attorney (in pro per). U Do it
								Legal can help me prepare my family law and give me information
								about different community resources. U Do It Legal is NOT my
								attorney. Because U Do It Legal is not my attorney, the service
								will not be giving me legal advice (unless separately
								purchased). Because U Do It Legal is not my attorney, it is also
								available to help the other side if they purchase our services
								for assistance.
							</p>
							<hr />
							<h6>
								Please acknolwedge that you understand each of the following
								statements.
							</h6>
							<Form noValidate validated={validated} onSubmit={handleSubmit}>
								{submissionQuestions.map((question, index) => (
									<div className="initial" key={question}>
										<p>{question}</p>
										<Form.Group controlId={`question=${index + 1}`}>
											<Form.Label className="submission-label">
												Initial
											</Form.Label>
											<Form.Control required type="text" />
										</Form.Group>
									</div>
								))}
								<hr />
								<Row>
									<Col xs={5}>
										<Form.Group controlId="e-signature">
											<Form.Label>E-Signature</Form.Label>
											<Form.Control required type="text" />
										</Form.Group>
									</Col>
									<Col xs={4}>
										<Form.Group controlId="date">
											<Form.Label className="submission-label">Date</Form.Label>
											<Form.Control required type="date" />
										</Form.Group>
									</Col>
								</Row>
								<Button type="submit" className="submission-button">
									<p>Submit Eligibility Quiz</p>
								</Button>
							</Form>
						</div>
					)}
				</div>
			</div>
			<Footer />
		</div>
	);
};

export default Register;
